mapper.select:
select id,course_id,course_lecturer,course_chapter_name,ispay,addtime,orders from course_chapter
where  1=1

and course_id=#{courseId}


and course_chapter_name like concat(concat('%',#{courseChapterName},'%'))


and course_type_name=#{courseTypeName}


and course_type_subclass_name=#{courseTypeSubclassName}

order by orders asc

limit #{pageNo},#{pageSize}

select count(id) from course_chapter
where  1=1

and course_id=#{courseId}


and course_type_name=#{courseTypeName}


and course_type_subclass_name=#{courseTypeSubclassName}


and course_chapter_name like concat(concat('%',#{courseChapterName},'%'))

select * from course_section
where 1=1

and  course_chapter_id=#{courseChapterId}

order by orders asc

limit #{pageNo},#{pageSize}

	SELECT ifnull(orders, 1) previousOrder
	
	FROM course_section    
	
    
    from course_chapter
    
    
	FROM course_class
	
    
    from course
    
    
    from course_nofree
    
    WHERE 1=1
    
	and course_chapter_id=#{comment}
	
    
    and course_type_subclass_name=#{comment}
    
    
    and course_type_subclass_name=#{comment}
    
    
    and course_type_subclass_name=#{comment}
    
    
    and course_type_subclass_name=#{comment}
    
    
    
    and orders  <  #{orders}
    order by orders desc
    
    
    
    and orders  >  #{orders}
    order by orders asc
    
    limit 0,1
