mapper.select:
select qbf.id id,user_id,bank_id,qbf.types types,status,qbf.addtime addtime,content, qbc.course_type_subclass_name course_type_subclass_name,qbc.name name,nickname from question_bank_feedback qbf left join user u on qbf.user_id=u.id left join question_bank qb on qb.id=qbf.bank_id left join question_bank_subcategory qbs on qbs.id=qb.sub_id left join question_bank_category qbc on qbs.category_id=qbc.id where status=#{status}
and nickname like concat(concat('%',#{nickname},'%'))
and course_type_subclass_name like concat(concat('%',#{courseTypeSubclassName},'%'))
and name like concat(concat('%',#{name},'%'))
order by addtime desc limit #{pageNo},#{pageSize}
