mapper.select:
select qbf.id id,user_id,category_id,bank_id,qbf.types types,status,qbf.addtime addtime,course_type_subclass_name,name
,nickname
from question_bank_feedback qbf
left join question_bank_category qbc on qbf.category_id=qbc.id 
left join user u on qbf.user_id=u.id
left join question_bank qb on qb.id=qbf.bank_id
where status=#{status}

nickname=#{nickname}


course_type_subclass_name=#{courseTypeSubclassName}


name=#{name}

order by addtime desc limit #{pageNo},#{pageSize}
