mapper.select:
select ubc.*,u.nickname nickname,u.realname realname,u.mobile mobile,bbh.sales_people salesPeople,bbh.course_ways courseways from user_buy_course ubc left join user u on u.id=ubc.user_id left join business_book_history bbh on bbh.buy_course_id=ubc.id where 1=1
and (u.realname like concat(concat('%',#{nicknameORmobile},'%')) or u.mobile like concat(concat('%',#{nicknameORmobile},'%')))
and UNIX_TIMESTAMP(ubc.addtime)>=UNIX_TIMESTAMP(#{fromdate})
and UNIX_TIMESTAMP(ubc.addtime)<UNIX_TIMESTAMP(#{todate})
and business_id=#{businessId}
and class_id is not null
and course_id is not null
and class_id=#{classId}
and course_id=#{courseId}
order by ubc.addtime desc
limit #{pageNo},#{pageSize}

select count(ubc.id) from user_buy_course ubc left join user u on u.id=ubc.user_id left join business_book_history bbh on bbh.buy_course_id=ubc.id where 1=1
and (u.realname like concat(concat('%',#{nicknameORmobile},'%')) or u.mobile like concat(concat('%',#{nicknameORmobile},'%')))
and UNIX_TIMESTAMP(ubc.addtime)>=UNIX_TIMESTAMP(#{fromdate})
and UNIX_TIMESTAMP(ubc.addtime)<UNIX_TIMESTAMP(#{todate})
and business_id=#{businessId}
and class_id is not null
and course_id is not null
and class_id=#{classId}
and course_id=#{courseId}

select count(id) from user_buy_course where 1=1

and user_id=#{userId}


and class_id=#{classId}


and course_id=#{courseId}

and status=0

mapper.update:
update user_buy_course set status=1 where 1=1

and class_id=#{classId}


and course_id=#{courseId}

mapper.select:
select ubc.user_id from user_buy_course as ubc 
right join course_class as cc
on ubc.class_id = cc.id 
left join user as u 
on u.id = ubc.user_id
where 1=1 

and class_id = #{classId}


and u.sex = #{sex}


and u.userrole = #{userrole}


and u.isdoctor = #{isdoctor}


and u.birthday like (concat('%',#{birthday},'%'))

select c.user_id from  course as c
left join user_buy_course as ubc
on ubc.course_id = c.id
left join user as u 
on u.id = ubc.user_id
where 1=1 

and c.id = #{courseId}
 

and u.sex = #{sex}


and u.userrole = #{userrole}


and u.isdoctor = #{isdoctor}


and u.birthday like (concat('%',#{birthday},'%'))
