mapper.select:
		select * from user
		where 1=1
		
			and mobile LIKE CONCAT(CONCAT('%',#{mobile},'%'))
		
		
			and nickname LIKE CONCAT(CONCAT('%',#{nickname},'%'))
		
		
			and userrole=#{userrole}
		

		order by addtime desc limit #{pageNo},#{pageSize}

select * from user_buy_course as us left join user as u on u.id = us.user_id group by us.user_id having 1=1
and UNIX_TIMESTAMP(us.addtime)>=UNIX_TIMESTAMP(#{fromdate})
and UNIX_TIMESTAMP(us.addtime)<UNIX_TIMESTAMP(#{todate})
and (u.realname like concat(concat('%',#{realnameORmobile},'%')) or u.mobile like concat(concat('%',#{realnameORmobile},'%')))
and u.isdisabled=#{status}
order by us.addtime desc limit #{pageNo},#{pageSize}

select nickname from user
where nickname=#{username} and
password=#{password}

select id from user where 1=1

	and sex = #{sex}


	and userrole = #{userrole}


	and isdoctor = #{isdoctor}


	and birthday like (concat('%',#{birthday},'%'))
